import("https://esm.sh/d3@7.9.0").then((d3) => {

// src/panels/browser_scripts/tooltip.ts
class Tooltip {
  getTooltipContent;
  el;
  constructor(container, getTooltipContent) {
    this.getTooltipContent = getTooltipContent;
    this.el = d3.select(document.createElement("div")).style("position", "absolute").style("pointer-events", "none").style("top", 0).style("opacity", 0).style("background", "white").style("color", "black").style("border-radius", "5px").style("box-shadow", "0 0 10px rgba(0,0,0,.25)").style("padding", "10px").style("line-height", "1.3").style("font", "11px sans-serif");
    container.appendChild(this.el.node());
  }
  showOnMouseover(event, d) {
    const content = this.getTooltipContent(d);
    this.el.style("opacity", 1).html(content);
    this.move(event, d);
  }
  hideOnMouseleave(_event, _d) {
    this.el.style("opacity", 0);
  }
  move(event, _d) {
    const element = this.el.node();
    if (!element)
      return;
    const container = element.parentNode;
    const [x, y] = d3.pointer(event, container);
    const area = container.getBoundingClientRect();
    const tooltipRect = element.getBoundingClientRect();
    let topicLeft = x + 10;
    if (topicLeft + tooltipRect.width > area.width) {
      topicLeft = Math.max(0, x - (10 + tooltipRect.width + 30));
    }
    let topicTop = y;
    if (topicTop + tooltipRect.height > area.height) {
      topicTop = Math.max(0, area.height - tooltipRect.height);
    }
    this.el.style("left", `${topicLeft}px`).style("top", `${topicTop}px`);
  }
}

// src/panels/browser_scripts/utils.ts
function newSvgArea(container) {
  const rect = container.parentNode.getBoundingClientRect();
  const width = rect.width;
  const height = rect.height;
  const svg = d3.create("svg").attr("width", "93%").attr("font-family", "sans-serif").style("display", "block");
  svg.attr("viewBox", [0, 0, width, height]).attr("preserveAspectRatio", "none");
  container.replaceChildren(svg.node());
  return { svg, width, height };
}

// src/panels/browser_scripts/draw_barchart_double.ts
function draw(context) {
  const withQueuedDuration = true;
  const visibleBars = 20;
  const data = (context.data[0] || []).reverse().slice(0, visibleBars);
  const marginTop = 20;
  const marginRight = 20;
  const marginBottom = 20;
  const marginLeft = 50;
  const container = context.element;
  const { svg, width, height } = newSvgArea(container);
  const tooltip = new Tooltip(container, (d) => {
    const formatDuration = (t) => t ? `${t / 60 | 0}m ${t % 60}s` : "";
    let adds = "";
    if (withQueuedDuration) {
      adds += `<br/>queued: ${formatDuration(d.queued_duration)}`;
    }
    return `<b>${d3.isoFormat(new Date(d.at))}</b>
        <br/>run id: ${d.subject_id || "-"}
        <br/>result: ${d.outcome || "-"}
        <br/>run: ${formatDuration(d.run_duration)}
        ${adds}
        <br/><br/><i>click for details</i>
        `;
  });
  const x = d3.scaleBand().domain(d3.range(visibleBars)).range([marginLeft, width - marginRight]).padding(0.1);
  const defaultQueuedDisplayed = 2;
  const defaultRunDisplayed = 10;
  const y = d3.scaleLinear().domain([
    -1 * Math.max(30, d3.max(data, (d) => d.queued_duration) || defaultQueuedDisplayed),
    Math.max(60, d3.max(data, (d) => d.run_duration) || defaultRunDisplayed)
  ]).range([height - marginBottom, marginTop]);
  const colors = d3.scaleOrdinal().domain([
    "success",
    "pass",
    "ok",
    "failure",
    "fail",
    "cancel",
    "cancelled",
    "skip",
    "skipped"
  ]).range([
    d3.hsl(120, 1, 0.5),
    d3.hsl(120, 1, 0.5),
    d3.hsl(120, 1, 0.5),
    d3.hsl(0, 1, 0.5),
    d3.hsl(0, 1, 0.5),
    d3.hsl(0, 0, 0.8),
    d3.hsl(0, 0, 0.8),
    d3.hsl(0, 0, 0.5),
    d3.hsl(0, 0, 0.5)
  ]).unknown(d3.hsl(229, 1, 0.5));
  const interval = 60;
  const axis = svg.append("g");
  const ticks = d3.range(Math.round(y.domain()[0] / interval) * interval, Math.round(y.domain()[1] / interval) * interval + 1, interval);
  axis.append("g").selectAll().data(ticks).join("text").attr("fill", d3.hsl(0, 0, 0.9).toString()).attr("x", 0).attr("y", (d) => y(d)).attr("dy", -3).attr("text-anchor", "start").text((d) => `${Math.abs(d)}s`);
  axis.append("g").selectAll().data(ticks).join("line").attr("stroke", (d) => d3.hsl(0, 0, d === 0 ? 0.9 : 0.6).toString()).attr("x1", marginLeft).attr("y1", (d) => y(d)).attr("x2", width - marginRight).attr("y2", (d) => y(d));
  svg.append("g").selectAll().data(data).join("rect").attr("fill", (d) => colors(d.outcome).copy({ opacity: 0.6 }).toString()).attr("stroke", (d) => colors(d.outcome).toString()).attr("x", (_d, i) => x(i) || 0).attr("y", (d) => y(d.run_duration || defaultRunDisplayed)).attr("height", (d) => y(0) - y(d.run_duration || defaultRunDisplayed)).attr("width", x.bandwidth()).on("click", (_event, d) => {
    if (d.url) {
      window.open(d.url, "_blank");
    }
  }).on("mouseleave", (e, d) => tooltip.hideOnMouseleave(e, d)).on("mouseover", (e, d) => tooltip.showOnMouseover(e, d));
  if (withQueuedDuration) {
    svg.append("g").selectAll().data(data).join("rect").attr("fill", (d) => colors(d.outcome).toString()).attr("stroke", (d) => colors(d.outcome).toString()).attr("x", (_d, i) => x(i) || 0).attr("y", (_d, _i) => y(0)).attr("height", (d) => y(0) - y(d.queued_duration || defaultQueuedDisplayed)).attr("width", x.bandwidth()).on("mouseleave", (e, d) => tooltip.hideOnMouseleave(e, d)).on("mouseover", (e, d) => tooltip.showOnMouseover(e, d));
  }
}

draw(context);});
